contracts/NPADStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title NPADStaking
 * @dev Simple staking contract for NPAD tokens to qualify for launchpad tiers
 */
contract NPADStaking is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    IERC20 public immutable stakingToken;
    
    uint256 private _totalSupply;
    mapping(address => uint256) private _balances;

    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);

    constructor(address _stakingToken, address initialOwner) Ownable(initialOwner) {
        require(_stakingToken != address(0), "Invalid token address");
        stakingToken = IERC20(_stakingToken);
    }

    /**
     * @dev Returns total amount of tokens staked
     */
    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev Returns staked balance of a user
     */
    function stakedBalance(address account) external view returns (uint256) {
        return _balances[account];
    }

    /**
     * @dev User stakes NPAD tokens
     */
    function stake(uint256 amount) external nonReentrant {
        require(amount > 0, "Cannot stake 0");
        
        _totalSupply += amount;
        _balances[msg.sender] += amount;
        
        stakingToken.safeTransferFrom(msg.sender, address(this), amount);
        
        emit Staked(msg.sender, amount);
    }

    /**
     * @dev User withdraws staked tokens
     */
    function withdraw(uint256 amount) external nonReentrant {
        require(amount > 0, "Cannot withdraw 0");
        require(_balances[msg.sender] >= amount, "Insufficient staked balance");
        
        _totalSupply -= amount;
        _balances[msg.sender] -= amount;
        
        stakingToken.safeTransfer(msg.sender, amount);
        
        emit Withdrawn(msg.sender, amount);
    }
}

contracts/Presale.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

/**
 * @title IUniswapV2Router02
 */
interface IUniswapV2Router02 {
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);
}

/**
 * @title NoblePadPresale
 * @notice Tiered Presale System with deterministic allocation and roll-down logic.
 */
contract Presale is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    enum State { Upcoming, Live, Success, Failed }

    // --- Configuration ---
    IERC20 public immutable token;
    IUniswapV2Router02 public immutable router;
    
    uint256 public immutable softCap;
    uint256 public immutable hardCap;
    uint256 public immutable presaleRate;
    uint256 public immutable listingRate;
    uint256 public immutable liquidityPercent;
    
    uint256 public immutable startTime;
    uint256 public immutable endTime;
    uint256 public immutable lockPeriod; // 365 days Cliff
    
    // --- Tier System ---
    bytes32 public immutable snapshotRoot; // Merkle Root of (address, tier, maxAlloc)
    
    uint8 public constant TIER_BRONZE = 1;
    uint8 public constant TIER_SILVER = 2;
    uint8 public constant TIER_GOLD = 3;

    // Splits: Bronze 50%, Silver 30%, Gold 20%
    uint256 public constant SPLIT_BRONZE = 50;
    uint256 public constant SPLIT_SILVER = 30;
    uint256 public constant SPLIT_GOLD = 20;

    mapping(uint8 => uint256) public tierTotalRaised; // ETH raised per tier
    mapping(uint8 => uint256) public tierAllocations; // Final Token Allocation per tier
    mapping(address => uint8) public userTier;        // Track user tier
    
    // --- State Variables ---
    uint256 public totalRaised;
    bool public finalized;
    uint256 public finalizeTime;
    uint256 public constant MIN_LIQUIDITY_PERCENT = 60;
    
    mapping(address => uint256) public contributions;
    mapping(address => bool) public hasClaimed;

    // --- Events ---
    event PresaleInitialized(address indexed token, address indexed owner, uint256 amount);
    event Contribution(address indexed buyer, uint8 tier, uint256 amount);
    event PresaleFinalized(uint256 totalRaised, uint256 liquidityAdded, uint256 tokensLocked);
    event RefundClaimed(address indexed buyer, uint256 amount);
    event TokensClaimed(address indexed buyer, uint256 tokenAmount, uint256 ethRefund);
    event TierAllocationSet(uint8 tier, uint256 amount);

    constructor(
        address _token,
        address _router,
        uint256 _softCap,
        uint256 _hardCap,
        uint256 _presaleRate,
        uint256 _listingRate,
        uint256 _liquidityPercent,
        uint256 _startTime,
        uint256 _endTime,
        uint256 _lockPeriod,
        uint256 /* _maxSpendPerBuyer */, // Moved to Merkle Proof
        uint256 _amount,
        bytes32 _snapshotRoot,
        address _owner
    ) Ownable(_owner) {
        require(_token != address(0), "Invalid token");
        require(_router != address(0), "Invalid router");
        require(_softCap > 0 && _softCap <= _hardCap, "Invalid caps");
        require(_startTime > block.timestamp, "Start time in past");
        require(_endTime > _startTime, "End time before start");
        require(_liquidityPercent >= MIN_LIQUIDITY_PERCENT && _liquidityPercent <= 100, "Invalid liquidity%");
        
        token = IERC20(_token);
        router = IUniswapV2Router02(_router);
        softCap = _softCap;
        hardCap = _hardCap;
        presaleRate = _presaleRate;
        listingRate = _listingRate;
        liquidityPercent = _liquidityPercent;
        startTime = _startTime;
        endTime = _endTime;
        lockPeriod = _lockPeriod;
        snapshotRoot = _snapshotRoot;

        emit PresaleInitialized(_token, _owner, _amount);
    }

    function getState() public view returns (State) {
        if (finalized) return State.Success;
        if (block.timestamp < startTime) return State.Upcoming;
        if (block.timestamp >= startTime && block.timestamp <= endTime) return State.Live;
        if (totalRaised >= softCap) return State.Success;
        return State.Failed;
    }

    // --- Tier Contribution ---

    function contribute(bytes32[] calldata proof, uint8 tier, uint256 maxAlloc) public payable nonReentrant {
        require(getState() == State.Live, "Presale not live");
        require(msg.value > 0, "Amount must be > 0");
        
        // Merkle Verification
        if (msg.sender != owner()) { // Owner can bypass for testing/emergency
            bytes32 leaf = keccak256(abi.encodePacked(msg.sender, tier, maxAlloc));
            require(MerkleProof.verify(proof, snapshotRoot, leaf), "Invalid Merkle Proof");
        }

        require(contributions[msg.sender] + msg.value <= maxAlloc, "Exceeds max allocation");

        // Note: We allow totalRaised to exceed hardCap for Oversubscription logic!
        // The excess will be refunded during claim.

        contributions[msg.sender] += msg.value;
        totalRaised += msg.value;
        tierTotalRaised[tier] += msg.value;
        userTier[msg.sender] = tier;

        emit Contribution(msg.sender, tier, msg.value);
    }

    // --- Finalization with Roll-Down & Scaling ---

    function finalize() external nonReentrant {
        State state = getState();
        require(state == State.Success || (block.timestamp > endTime && totalRaised >= softCap), "Cannot finalize");
        require(!finalized, "Already finalized");

        finalized = true;
        finalizeTime = block.timestamp;

        // 1. Determine Total Tokens for Sale (Capped at hardCap equivalent)
        // If we raised MORE than hardCap, we only distribute hardCap worth of tokens.
        uint256 effectiveRaised = totalRaised > hardCap ? hardCap : totalRaised;
        uint256 totalTokensForSale = effectiveRaised * presaleRate;

        // 2. Initial Pool Splits
        uint256 poolGold = (totalTokensForSale * SPLIT_GOLD) / 100;
        uint256 poolSilver = (totalTokensForSale * SPLIT_SILVER) / 100;
        uint256 poolBronze = totalTokensForSale - poolGold - poolSilver; // Remainder to Bronze

        // 3. Roll-Down Logic (Gold -> Silver -> Bronze)
        
        // Demand (In Tokens)
        uint256 demandGold = tierTotalRaised[TIER_GOLD] * presaleRate;
        uint256 demandSilver = tierTotalRaised[TIER_SILVER] * presaleRate;
        // Bronze demands everything else implicitly, but we calculate it
        // uint256 demandBronze = tierTotalRaised[TIER_BRONZE] * presaleRate;

        // Gold -> Silver
        if (demandGold < poolGold) {
            uint256 surplus = poolGold - demandGold;
            poolGold = demandGold; // Gold gets exactly what they wanted
            poolSilver += surplus; // Surplus flows to Silver
        }
        tierAllocations[TIER_GOLD] = poolGold;

        // Silver -> Bronze
        if (demandSilver < poolSilver) {
            uint256 surplus = poolSilver - demandSilver;
            poolSilver = demandSilver; // Silver gets exactly what they wanted
            poolBronze += surplus; // Surplus flows to Bronze
        }
        tierAllocations[TIER_SILVER] = poolSilver;
        tierAllocations[TIER_BRONZE] = poolBronze;

        emit TierAllocationSet(TIER_GOLD, poolGold);
        emit TierAllocationSet(TIER_SILVER, poolSilver);
        emit TierAllocationSet(TIER_BRONZE, poolBronze);

        // 4. Liquidity & Fees (Based on Effective Raised)
        uint256 nativeForLiquidity = (effectiveRaised * liquidityPercent) / 100;
        uint256 tokensForLiquidity = nativeForLiquidity * listingRate;

        token.forceApprove(address(router), tokensForLiquidity);
        try router.addLiquidityETH{value: nativeForLiquidity}(
            address(token),
            tokensForLiquidity,
            0, 0, // Slippage unchecked for simplicity in deployment, user should configure
            owner(),
            block.timestamp + 600
        ) {
            emit PresaleFinalized(totalRaised, nativeForLiquidity, tokensForLiquidity);
        } catch {
            // Fallback: Send liquidity funds to owner if router fails
            payable(owner()).transfer(nativeForLiquidity);
        }

        // Send remaining ETH (Fees/Dev) to Owner
        uint256 remainingETH = address(this).balance - (totalRaised - effectiveRaised); // Keep refunds!
        // Note: logic above needs care. We hold ALL ETH.
        // We actally need to keep: "effectiveRaised - nativeForLiquidity" for the owner?
        // NO. Oversubscription refunds come from the `totalRaised - effectiveRaised` portion.
        // But also, if a Tier is scaled down, we refund the diff.
        // So we should NOT withdraw "remainingETH" yet. We only withdraw the Liquidity portion and the Project portion.
        
        // Safe approach: Withdraw Project Funds = (effectiveRaised * (100 - liquidityPercent)) / 100
        uint256 projectFunds = (effectiveRaised * (100 - liquidityPercent)) / 100;
        payable(owner()).transfer(projectFunds);
    }

    // --- Claim with Lock & Refund ---

    function claimTokens() external nonReentrant {
        require(finalized, "Not finalized");
        require(block.timestamp >= finalizeTime + lockPeriod, "Tokens are locked");
        require(contributions[msg.sender] > 0, "No contribution");
        require(!hasClaimed[msg.sender], "Already claimed");

        hasClaimed[msg.sender] = true;
        uint8 tier = userTier[msg.sender];
        uint256 contribution = contributions[msg.sender];
        
        // Calculate Allocation
        // userShare = (contribution / tierTotalRaised) * tierPool
        uint256 tierTotal = tierTotalRaised[tier];
        uint256 tierPool = tierAllocations[tier];
        
        uint256 allocatedTokens = (contribution * tierPool) / tierTotal;
        uint256 idealTokens = contribution * presaleRate;

        // Refund Logic if Scaled Down
        uint256 ethRefund = 0;
        if (allocatedTokens < idealTokens) {
            // They paid for 'idealTokens' but got 'allocatedTokens'
            // Refund the ETH difference
            // diffTokens = idealTokens - allocatedTokens
            // refundETH = diffTokens / presaleRate
            uint256 diffTokens = idealTokens - allocatedTokens;
            ethRefund = diffTokens / presaleRate;
        }

        if (allocatedTokens > 0) {
            token.safeTransfer(msg.sender, allocatedTokens);
        }
        if (ethRefund > 0) {
            payable(msg.sender).transfer(ethRefund);
        }

        emit TokensClaimed(msg.sender, allocatedTokens, ethRefund);
    }

    function withdrawUnsoldTokens() external onlyOwner {
        require(finalized && block.timestamp > finalizeTime + lockPeriod + 30 days, "Too early");
        uint256 balance = token.balanceOf(address(this));
        token.safeTransfer(owner(), balance);
    }
    
    // Emergency withdraw only if failed
    function claimRefund() external nonReentrant {
        require(getState() == State.Failed, "Not failed");
        uint256 amount = contributions[msg.sender];
        require(amount > 0, "Nothing to refund");
        contributions[msg.sender] = 0;
        payable(msg.sender).transfer(amount);
        emit RefundClaimed(msg.sender, amount);
    }
}

