// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./Presale.sol";

contract PresaleFactory {
    event PresaleCreated(address indexed presale, address indexed owner, address indexed token);

    address public owner;
    address public tokenLock;
    uint256 public creationFee = 0.01 ether;

    constructor(address _tokenLock) {
        owner = msg.sender;
        tokenLock = _tokenLock;
    }

    function createPresale(
        address _token,
        address _router,
        uint256 _softCap,
        uint256 _hardCap,
        uint256 _presaleRate,
        uint256 _listingRate,
        uint256 _liquidityPercent,
        uint256 _startTime,
        uint256 _endTime,
        uint256 _lockPeriod,
        uint256 _maxSpendPerBuyer,
        uint256 _amount
    ) external payable returns (address) {
        require(msg.value >= creationFee, "Insufficient fee");
        Presale presale = new Presale(
            _token,
            _router,
            _softCap,
            _hardCap,
            _presaleRate,
            _listingRate,
            _liquidityPercent,
            _startTime,
            _endTime,
            _lockPeriod,
            _maxSpendPerBuyer,
            _amount,
            tokenLock,
            msg.sender
        );
        emit PresaleCreated(address(presale), msg.sender, _token);
        return address(presale);
    }
}

contracts/TokenLock.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title NoblePadTokenLock
 * @notice Secure token locker for liquidity and team tokens.
 */
contract TokenLock is ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct Lock {
        address token;
        address owner;
        uint256 amount;
        uint256 unlockTime;
        bool claimed;
        string description;
    }
    
    mapping(uint256 => Lock) public locks;
    uint256 public nextLockId;

    event TokensLocked(uint256 indexed lockId, address indexed owner, address indexed token, uint256 amount, uint256 unlockTime);
    event TokensClaimed(uint256 indexed lockId, address indexed owner, uint256 amount);

    /**
     * @notice Lock tokens for a specific period
     * @param token Address of the token to lock
     * @param amount Amount of tokens to lock
     * @param unlockTime Timestamp when tokens can be claimed
     * @param description Brief description of the lock (e.g. "Liquidity lock")
     */
    function lockTokens(address token, uint256 amount, uint256 unlockTime, string calldata description) external nonReentrant returns (uint256) {
        require(amount > 0, "Amount must be > 0");
        require(unlockTime > block.timestamp, "Unlock time must be in future");
        
        uint256 lockId = nextLockId++;
        locks[lockId] = Lock(token, msg.sender, amount, unlockTime, false, description);
        
        // Transfer tokens from user to this contract
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        
        emit TokensLocked(lockId, msg.sender, token, amount, unlockTime);
        return lockId;
    }

    /**
     * @notice Claim locked tokens once unlock time has passed
     * @param lockId The ID of the lock to claim
     */
    function claimTokens(uint256 lockId) external nonReentrant {
        Lock storage l = locks[lockId];
        require(msg.sender == l.owner, "Not owner");
        require(!l.claimed, "Already claimed");
        require(block.timestamp >= l.unlockTime, "Not unlocked yet");
        
        l.claimed = true;
        IERC20(l.token).safeTransfer(msg.sender, l.amount);
        
        emit TokensClaimed(lockId, msg.sender, l.amount);
    }

    /**
     * @notice Helper to get all locks for a user (Simplified view)
     */
    function getLock(uint256 lockId) external view returns (Lock memory) {
        return locks[lockId];
    }
}

contracts/TreasuryTimelock.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

/*
  Minimal Timelock controller for treasury actions.
  For production use, prefer OpenZeppelin TimelockController or Gnosis Safe + delegatecall patterns.
*/

import "@openzeppelin/contracts/access/Ownable.sol";

contract TreasuryTimelock is Ownable {
    uint256 public delay; // seconds

    event Scheduled(bytes32 indexed txHash, address indexed target, uint256 value, bytes data, uint256 when);
    event Executed(bytes32 indexed txHash, address indexed target, uint256 value, bytes data);

    constructor(uint256 _delay, address initialOwner) Ownable(initialOwner) {
        delay = _delay;
    }

    function schedule(address target, uint256 value, bytes calldata data, uint256 when) external onlyOwner returns (bytes32) {
        require(when >= block.timestamp + delay, "Must satisfy delay");
        bytes32 txHash = keccak256(abi.encode(target, value, data, when));
        emit Scheduled(txHash, target, value, data, when);
        return txHash;
    }

    function execute(address target, uint256 value, bytes calldata data, uint256 when) external onlyOwner payable returns (bytes memory) {
        require(block.timestamp >= when, "Not ready");
        bytes32 txHash = keccak256(abi.encode(target, value, data, when));
        (bool success, bytes memory result) = target.call{value: value}(data);
        require(success, "Execution failed");
        emit Executed(txHash, target, value, data);
        return result;
    }
}

contracts/Vesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title NoblePadVesting
 * @notice Secure linear vesting contract with cliff and duration.
 */
contract Vesting is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct Schedule {
        address beneficiary;
        address token;
        uint256 totalAmount;
        uint256 start; // timestamp
        uint256 cliff; // seconds from start
        uint256 duration; // total duration in seconds
        uint256 released;
    }

    uint256 public nextScheduleId;
    mapping(uint256 => Schedule) public schedules;

    event VestingCreated(uint256 indexed vestingId, address indexed beneficiary, uint256 totalAmount, uint256 start, uint256 duration);
    event Released(uint256 indexed vestingId, uint256 amount);

    constructor(address initialOwner) Ownable(initialOwner) {}

    /**
     * @notice Creates a new vesting schedule for a beneficiary.
     * Tokens are transferred from the caller to this contract.
     */
    function createVesting(
        address beneficiary,
        address token,
        uint256 totalAmount,
        address source, // Address to pull tokens from (must be approved)
        uint256 start,
        uint256 cliff,
        uint256 duration
    ) external onlyOwner nonReentrant returns (uint256) {
        require(beneficiary != address(0), "Invalid beneficiary");
        require(totalAmount > 0, "Amount must be > 0");
        require(duration > 0, "Duration must be > 0");
        
        uint256 vestingId = nextScheduleId++;
        schedules[vestingId] = Schedule(beneficiary, token, totalAmount, start, cliff, duration, 0);
        
        // Securely pull tokens from the source (usually the project owner)
        IERC20(token).safeTransferFrom(source, address(this), totalAmount);
        
        emit VestingCreated(vestingId, beneficiary, totalAmount, start, duration);
        return vestingId;
    }

    /**
     * @notice Returns the amount of tokens currently available to release for a schedule.
     */
    function releasableAmount(uint256 vestingId) public view returns (uint256) {
        Schedule storage s = schedules[vestingId];
        if (block.timestamp < s.start + s.cliff) return 0;
        
        uint256 elapsed = block.timestamp - s.start;
        if (elapsed >= s.duration) {
            return s.totalAmount - s.released;
        } else {
            uint256 vested = (s.totalAmount * elapsed) / s.duration;
            return vested - s.released;
        }
    }

    /**
     * @notice Releases vested tokens to the beneficiary.
     */
    function release(uint256 vestingId) external nonReentrant {
        Schedule storage s = schedules[vestingId];
        require(msg.sender == s.beneficiary || msg.sender == owner(), "Not authorized");
        
        uint256 amount = releasableAmount(vestingId);
        require(amount > 0, "No releasable amount");
        
        s.released += amount;
        IERC20(s.token).safeTransfer(s.beneficiary, amount);
        
        emit Released(vestingId, amount);
    }

    /**
     * @notice Emergency function to withdraw stuck tokens (only if not scheduled)
     */
    function recoverERC20(address tokenAddress, uint256 tokenAmount) external onlyOwner {
        IERC20(tokenAddress).safeTransfer(owner(), tokenAmount);
    }
}
